import './App.css';
import {useState} from "react";


function App() {

  const [darkMode, setDarkMode] = useState(true);

  const handleClick = () => {
      setDarkMode(!darkMode);
  }

  const setMode = (style) => {
      let backgroundColor, textColor, buttonColor;

      if (darkMode) {
          backgroundColor = "black";
          textColor = 'white';
          buttonColor = "#303030";
      } else {
          backgroundColor = 'white';
          textColor = 'black';
          buttonColor = "#ffe291";
      }

      if (style === 1) {
          return backgroundColor;
      } else if (style === 2) {
          return textColor;
      } else {
            return buttonColor;
        }
  }

  function createNode(row, col) {
        return {row, col};
  }

  const createGrid = (rows, cols) => {
      const grid = [];
      for (let row = 0; row < rows; row++) {
          const currentRow = [];
          for (let col = 0; col < cols; col++) {
              currentRow.push(createNode(row, col));
          }
          grid.push(currentRow);
      }
      return grid;
  }

  const globalGrid = createGrid(30, 65);


  return (
      <div className="App" style={{backgroundColor: setMode(1), color: setMode(2)}}>
          <div className="header-container">
              <div className="darkmode-container">
                  <label className="darkmode-button" style={{backgroundColor: setMode(3)}}>

                      <input type="checkbox" className="darkmode-input" onClick={handleClick}></input>
                      <span className="darkmode-toggle">
                          <img className="moon" src="https://img.icons8.com/sf-regular/48/null/moon-symbol.png"/>
                      </span>
                      <img className="sun" src="https://img.icons8.com/material-outlined/24/000000/smiling-sun.png"/>

                  </label>
              </div>
              <div className="header">
                  <header>Visualizer</header>
              </div>
          </div>
          <div className="map-container">
              <div className="map">
                  {globalGrid.map((row, rowIndex) => {
                      return (
                            row.map((node, nodeIndex) => {
                                return (
                                    <div key={nodeIndex} className="node">
                                        <label className="node-label">
                                            <input type="checkbox" className="node-input"></input>
                                        </label>
                                    </div>
                                )
                            })
                      )
                  })}
              </div>
          </div>
      </div>

  );
}

export default App;
