import logo from './logo.svg';
import './App.css';
import {useState} from "react";

function App() {
  const [bgc, setBgc] = useState('black');

  const handleClick = () => {
    if (bgc === 'teal') {
      setBgc('aqua');
    } else {
      setBgc('teal');
    }
  }

  return (
    <div className="App">
        <header className="App-header">
          Visualizer
        </header>
      <input className="switch" type="button" value="Change Color" onClick={handleClick}/>

    </div>
  );
}

export default App;
