import logo from './logo.svg';
import './App.css';
import {useState} from "react";

function App() {
  const [bgc, setBgc] = useState('teal');

  const handleClick = () => {
    if (bgc === 'teal') {
      setBgc('aqua');
    } else {
      setBgc('teal');
    }
  }

  return (
    <div className="App" style={{backgroundColor: bgc}}>
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
        <div>
          <button className='BGC-button' onClick={handleClick}>Change BG Color</button>
        </div>

      </header>
    </div>
  );
}

export default App;
