  }

  const setMode = (style) => {
      let backgroundColor, textColor, buttonColor;

      if (darkMode) {
          backgroundColor = "black";
          textColor = 'white';
          buttonColor = "#303030";
      } else {
          backgroundColor = 'white';
          textColor = 'black';
          buttonColor = "#ffe291";
      }

      if (style === 1) {
          return backgroundColor;
      } else if (style === 2) {
          return textColor;
      } else {
            return buttonColor;
        }
  }

