        return {row,
                col,
                isStart: row === start_row && col === start_col,
                isFinish: row === end_row && col === end_col,
                isMousePressed: false,
        };
  }

  const createGrid = (rows, cols) => {
      const grid = [];
      for (let row = 0; row < rows; row++) {
          const currentRow = [];
