import './App.css';
import {useState} from "react";

function App() {
  const [darkMode, setDarkMode] = useState(true);

  const handleClick = () => {
      setDarkMode(!darkMode);
  }

  const setMode = (style) => {
      let backgroundColor, textColor;

      if (darkMode) {
          backgroundColor = "black";
          textColor = 'white';
      } else {
          backgroundColor = 'white';
          textColor = 'black';
      }

      return style === 1 ? backgroundColor : textColor;
  }
  return (
      <body >
          <div className="App" style={{backgroundColor: setMode(1), color: setMode(2)}}>
              <div className="header-container">
                  <div className="darkmode-container">
                      <label className="darkmode-button" onClick={handleClick}>

                          <input type="checkbox" className="darkmode-input" ></input>
                          <span className="darkmode-toggle">

                              <img className="moon" src="https://img.icons8.com/sf-regular/48/null/moon-symbol.png"/>
                          </span>
                          <img className="sun" src="https://img.icons8.com/material-outlined/24/000000/smiling-sun.png"/>

                      </label>
                  </div>
                  <div className="header">
                      <header>Visualizer</header>
                  </div>
              </div>
          </div>
      </body>

  );
}

export default App;
