import logo from './logo.svg';
import './App.css';
import {useState} from "react";

function App() {
  const [bgc, setBgc] = useState('teal');

  const handleClick = () => {
    if (bgc === 'teal') {
      setBgc('aqua');
    } else {
      setBgc('teal');
    }
  }

  return (
    <div className="App" style={{backgroundColor: bgc}}>
        <header className="App-header">
          Visualizer
        </header>
    </div>
  );
}

export default App;
