import './App.css';
import {useState} from "react";

function App() {
  const [darkMode, setDarkMode] = useState(false);

  const darkStlye = {
    backgroundColor: "black",
    fontColor: "white"
  }


  const handleClick = () => {
    setDarkMode(!darkMode);
  }


  return (
      <body>
          <div className="App">
              <div className="header-container">
                  <div className="darkmode-container">
                      <label className="darkmode-button" onClick={handleClick}>
                          <span className="darkmode-toggle"></span>
                          <input type="checkbox" className="darkmode-input"></input>
                          <img className="sun" src="https://img.icons8.com/material-outlined/24/null/smiling-sun.png"/>

                      </label>
                  </div>
                  <div className="header">
                      <header>Visualizer</header>
                  </div>
              </div>
          </div>
      </body>

  );
}

export default App;
